import React from 'react';
import Dropdown from '../dropdown/dropdown.js'
import filterData from '../filter/filterData.js'
// import ReactDOM from 'react-dom';
// import * as serviceWorker from './serviceWorker';

class Filter extends React.Component {
    reset = false
    dropdownComponents = filterData.map(filter => <Dropdown key = {filter.id} id = {filter.id} name = {filter.name} changeFilter = {this.props.changeFilter} reset={this.reset}/>)

    render() {
    const filter_style = {
        marginLeft: "40%"
    }
    return (  
                <div className="col-lg-3">
                        <div className="container" style={filter_style} >
                                {this.dropdownComponents}
                            <br/>
                            <button type="button" className="btn btn-info" onClick = {this.reset = !this.reset, this.props.resetFilters}>Reset Filters</button>
                        </div>

                        <br/>
                </div>
    )
    }
}


export default Filter;

